const summaryapiURL="https://api.openweathermap.org/data/2.5/weather?id=5607916&units=imperial&appid=36089ae9649ea39369703e6946f36ac3";fetch(summaryapiURL).then(e=>e.json()).then(e=>{document.getElementById("currently").textContent=e.weather[0].description,document.getElementById("current-temp").textContent=Math.round(e.main.temp),document.getElementById("humidity").textContent=e.main.humidity,document.getElementById("wind-speed").textContent=e.wind.speed;let t=e.main.temp,n=e.wind.speed;document.querySelector("#windchill").textContent="N/A",t<=50&&n>=3&&(document.querySelector("#windchill").innerHTML=Math.round(function(e,t){return 35.74+.6215*e-35.75*Math.pow(t,.16)+e*Math.pow(t,.16)*.4275}(t,n))+"&deg; F")});const forecastURL="https://api.openweathermap.org/data/2.5/forecast?id=5607916&units=imperial&appid=36089ae9649ea39369703e6946f36ac3";fetch(forecastURL).then(e=>e.json()).then(e=>{var t=new Array(7);t[0]="Sun",t[1]="Mon",t[2]="Tue",t[3]="Wed",t[4]="Thu",t[5]="Fri",t[6]="Sat";for(var n=document.getElementsByClassName("forecast-day"),a=document.getElementsByClassName("weatherIcon"),r=e.list.filter(e=>e.dt_txt.includes("18:00:00")),o=document.getElementsByClassName("forecast-data"),i=0;i<r.length;i++){var d=new Date(r[i].dt_txt);n[i].textContent=t[d.getDay()];const e="https://openweathermap.org/img/w/"+r[i].weather[0].icon+".png",m=r[i].weather[0].description;a[i].setAttribute("src",e),a[i].setAttribute("alt",m),o[i].innerHTML=Math.round(r[i].main.temp)+" &#176;F"}});